
public class Hello {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Hello, Abdul. "); // this line print the sentence inside the ()
		
		int myFirstNumber =  (10 + 5) + (2 * 10); // declared a variable to int and set value. 
		int mySecondNumber = 12; // declared a another int variable
		int myThirdNumber = myFirstNumber * 2; // declared another variable and set value to myFirstNumber multiply by 2
		
		int myTotal = myFirstNumber + mySecondNumber + myThirdNumber; // this variable will sum up the total 
		
		int myLastOne = 1000 - myTotal;
		
		System.out.println(myLastOne); // this line print out the variable from previous line.
		

	}

}
